import titleArea from "../assets/WorkModalTitleArea.png";
import exitx from "../assets/exitx.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/80 z-40 flex justify-center items-start overflow-y-auto pt-4 pl-4 pr-4"
      onClick={onClose}
    >
      {/* Modal wrapper: bigger width and full height */}
      <div
        className="relative w-full sm:w-3/4 max-w-[1200px] h-full flex flex-col"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Sticky header */}
        <div className="sticky top-0 z-50">
          <div className="relative w-full h-24 overflow-hidden">
            {" "}
            {/* smaller height */}
            <img
              src={titleArea}
              alt="Title Area"
              className="w-full h-full object-fill"
            />
            {/* Overlay text */}
            <div className="absolute inset-0 flex justify-between items-center px-20 py-4">
              <h2 className="text-4xl font-bold font-stretch-extra-expanded text-black">
                {workTitle}
              </h2>
              <button
                onClick={onClose}
                className="text-black text-3xl hover:opacity-70"
              >
                <img src={exitx} width="24" height="24" alt="Exit X" />
              </button>
            </div>
          </div>
        </div>

        {/* White panel slightly under the header */}
        <div className="bg-white w-full sm:w-[90%] mx-auto -mt-8 rounded-t-lg shadow-xl flex-1 overflow-y-auto ">
          <div className="p-4">{children}</div>
        </div>
      </div>
    </div>
  );
};

export default WorkModal;
