import WorkCardBorder from "./WorkCardBorder";
import React, { useState } from "react";
import WorkModal from "../components/WorkModal";

const WorkCard = ({ children, title, image, date, id }) => {
  const [openModalId, setOpenModalId] = useState(null);

  const openModal = (id) => {
    setOpenModalId(id);
  };

  const closeModal = () => {
    setOpenModalId(null);
  };

  return (
    <>
      <WorkCardBorder onClick={() => openModal(id)}>
        <div>
          <img
            src={image}
            alt={title}
            className="w-full h-auto object-cover -mt-4"
          />
        </div>
        <div className=" flex flex-col mt-5">
          <h3 className="text-3xl font-bold font-source_san_3 scale-x-55">
            {title}
          </h3>
          <p className="text-xl font-medium font-source_san_3">{date}</p>
        </div>
      </WorkCardBorder>
      <WorkModal
        workTitle="children"
        isOpen={openModalId === "modal1"}
        onClose={closeModal}
      >
        {children}
      </WorkModal>
    </>
  );
};

export default WorkCard;
