import workModalBackground from "../assets/WorkModalBackground.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/50 z-40 flex justify-center items-center p-4"
      onClick={onClose}
    >
      {/* Modal container with image background */}
      <div
        className="relative w-full sm:w-1/2 max-w-[800px] h-full sm:h-auto bg-cover bg-center shadow-xl rounded-lg overflow-hidden transform transition-transform duration-300"
        style={{ backgroundImage: `url(${workModalBackground})` }}
        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside
      >
        {/* Sticky header content inside the image */}
        <div className="sticky top-0 z-50 flex justify-between items-center p-4 bg-transparent">
          <h2 className="text-3xl font-bold font-stretch-extra-expanded text-black">
            {workTitle}
          </h2>
          <button
            onClick={onClose}
            className="text-black text-3xl hover:opacity-70"
          >
            &times;
          </button>
        </div>

        {/* Modal content inside image */}
        <div className="p-4 overflow-y-auto max-h-[80vh]">{children}</div>
      </div>
    </div>
  );
};

export default WorkModal;
