import React, { useState } from "react";
import WorkCard from "../components/WorkCard";
import DescriptionSection from "../components/DescriptionSection";
import ModalImageGrid from "../components/ModalImageGrid";

const WorkPage = () => {
  const [openModalId, setOpenModalId] = useState(null);

  // Define all work items here
  const workItems = [
    {
      id: "modal1",
      title: "20 To 10",
      date: "February 2025",
      image: require("../work_assets/icons/20to10_icon.avif"),
      children: (
        <>
          <ModalImageGrid folder={twentytotenPhotos} />
          <DescriptionSection
            tagline="I know less at 20, than I did at 10"
            description="What does it mean when you turn 20? Are you supposed to know everything? 
          Are you still supposed to be confused about everything around you? Inspired by grid collages and my personal feelings of being confused about everything at 20; 
          I made a collage of those feelings. Putting together things I've held dear to myself; music, my headphones, floral patterns. It's an all-encompassing collage of not only me at 20 but aspects of me at all stages of my life."
          />
        </>
      ),
    },
    {
      id: "modal2",
      title: "V00TING 1S C001",
      date: "November 2024",
      image: require("../work_assets/icons/Lamar_UpClose.webp"),
      children: <DescriptionSection />,
    },
    {
      id: "modal3",
      title: "Across The Amtrak",
      date: "August 2024",
      image: require("../work_assets/icons/Untitled-1.webp"),
      children: <DescriptionSection />,
    },
  ];

  return (
    <div className="flex flex-wrap justify-evenly m-4">
      {workItems.map((item) => (
        <WorkCard
          key={item.id}
          id={item.id}
          title={item.title}
          date={item.date}
          image={item.image}
          openModalId={openModalId}
          setOpenModalId={setOpenModalId}
        >
          {item.children}
        </WorkCard>
      ))}
    </div>
  );
};

export default WorkPage;
