import titleArea from "../assets/WorkModalTitleArea.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/50 z-40 flex justify-end"
      onClick={onClose}
    >
      <div
        className="relative w-full sm:w-1/2 h-full"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Sticky header ABOVE the panel */}
        <div className="sticky top-0 z-50">
          <div className="relative">
            <img
              src={titleArea}
              alt="Title Area"
              className="w-full scale-y-125  object-cover"
            />

            {/* Overlay text */}
            <div className="absolute inset-0 flex justify-between items-center p-4">
              <h2 className="text-3xl font-bold font-stretch-extra-expanded text-black">
                {workTitle}
              </h2>
              <button
                onClick={onClose}
                className="text-black text-3xl hover:opacity-70"
              >
                &times;
              </button>
            </div>
          </div>
        </div>

        {/* White modal panel */}
        <div className="bg-white h-full overflow-y-auto shadow-xl pt-32">
          {/* pt-32 MUST match header height */}
          <div className="p-1">{children}</div>
        </div>
      </div>
    </div>
  );
};

export default WorkModal;
