import titleArea from "../assets/WorkModalTitleArea.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black bg-opacity-50 z-40 flex justify-end"
      onClick={onClose}
    >
      <div
        className="bg-white w-full sm:w-1/2 h-full overflow-y-auto shadow-xl"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Sticky image header */}
        <div className="sticky top-0 z-50 relative">
          <img
            src={titleArea}
            alt="Title Area"
            className="w-full h-32 object-cover"
          />

          {/* Overlay content */}
          <div className="absolute inset-0 flex justify-between items-center p-4">
            <h2 className="text-3xl font-bold font-stretch-extra-expanded text-black">
              {workTitle}
            </h2>

            <button
              onClick={onClose}
              className="text-black text-3xl hover:opacity-70"
            >
              &times;
            </button>
          </div>
        </div>

        {/* Modal body */}
        <div className="p-1">{children}</div>
      </div>
    </div>
  );
};

export default WorkModal;
