import workModalBackground from "../assets/WorkModalBackground.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/50 z-50 flex justify-end"
      onClick={onClose}
    >
      {/* Modal container â€” 50% width */}
      <div
        className="relative w-1/2 h-screen overflow-hidden"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Background image */}
        <img
          src={workModalBackground}
          alt="Modal Background"
          className="absolute inset-0 w-full h-full object-fill"
        />

        {/* Content overlay */}
        <div className="relative z-10 flex flex-col h-full">
          {/* Sticky header */}
          <div className="sticky top-0 flex justify-between items-center p-6">
            <h2 className="text-3xl font-bold font-stretch-extra-expanded text-black ml-4">
              {workTitle}
            </h2>
            <button
              onClick={onClose}
              className="text-black text-3xl hover:opacity-70"
            >
              &times;
            </button>
          </div>

          {/* Scrollable body */}
          <div className="flex-1 overflow-y-auto p-6">{children}</div>
        </div>
      </div>
    </div>
  );
};

export default WorkModal;
