import titleArea from "../assets/WorkModalTitleArea.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/50 z-40 flex justify-center items-start overflow-y-auto"
      onClick={onClose}
    >
      {/* Modal wrapper: full height */}
      <div
        className="relative w-full sm:w-1/2 h-full flex flex-col"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Sticky header */}
        <div className="sticky top-0 z-50">
          <div className="relative h-32 overflow-hidden">
            <img
              src={titleArea}
              alt="Title Area"
              className="absolute left-0 right-0 top-1/2 w-full h-auto -translate-y-1/2 scale-y-50"
            />
            <div className="absolute inset-0 flex justify-between items-center p-4">
              <h2 className="text-3xl font-bold font-stretch-extra-expanded text-black">
                {workTitle}
              </h2>
              <button
                onClick={onClose}
                className="text-black text-3xl hover:opacity-70"
              >
                &times;
              </button>
            </div>
          </div>
        </div>

        {/* White panel: spans to bottom */}
        <div className="bg-white w-full sm:w-[90%] mx-auto -mt-8 rounded-t-lg shadow-xl flex-1 overflow-y-auto pt-32 pb-32">
          <div className="p-4">{children}</div>
        </div>
      </div>
    </div>
  );
};

export default WorkModal;
