import { useEffect, useState } from "react";
import titleArea from "../assets/WorkModalTitleArea.png";

const WorkModal = ({ isOpen, onClose, children, workTitle }) => {
  const [showModal, setShowModal] = useState(false);

  // Animate in when isOpen changes
  useEffect(() => {
    if (isOpen) setShowModal(true);
  }, [isOpen]);

  // Lock body scroll
  useEffect(() => {
    if (showModal) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }

    return () => {
      document.body.style.overflow = "";
    };
  }, [showModal]);

  if (!isOpen && !showModal) return null;

  const handleClose = () => {
    // Animate out first
    setShowModal(false);
    setTimeout(() => {
      onClose();
    }, 300); // match transition duration
  };

  const preventScrollPropagation = (e) => {
    const el = e.currentTarget;
    const scrollTop = el.scrollTop;
    const scrollHeight = el.scrollHeight;
    const offsetHeight = el.offsetHeight;
    const delta = e.deltaY;

    if (
      (delta > 0 && scrollTop + offsetHeight >= scrollHeight) ||
      (delta < 0 && scrollTop <= 0)
    ) {
      e.preventDefault();
    }
  };

  return (
    <div
      className="fixed inset-0 bg-black/50 z-40 flex justify-center items-start overflow-y-auto p-4"
      onClick={handleClose}
    >
      {/* Modal wrapper with slide-up animation */}
      <div
        className={`relative w-full sm:w-3/4 max-w-[1200px] h-full flex flex-col transform transition-transform duration-300
          ${
            showModal
              ? "translate-y-0 opacity-100"
              : "-translate-y-full opacity-0"
          }`}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Sticky header */}
        <div className="sticky top-0 z-50">
          <div className="relative w-full h-24 overflow-hidden">
            <img
              src={titleArea}
              alt="Title Area"
              className="w-full h-full object-fill"
            />
            <div className="absolute inset-0 flex justify-between items-center p-4">
              <h2 className="text-3xl font-bold font-stretch-extra-expanded text-black ml-8">
                {workTitle}
              </h2>
              <button
                onClick={handleClose}
                className="text-black text-3xl hover:opacity-70"
              >
                &times;
              </button>
            </div>
          </div>
        </div>

        {/* White panel */}
        <div
          className="bg-white w-full sm:w-[90%] mx-auto -mt-8 rounded-t-lg shadow-xl flex-1 overflow-y-auto "
          onWheel={preventScrollPropagation}
          onTouchMove={preventScrollPropagation}
        >
          <div className="p-4">{children}</div>
        </div>
      </div>
    </div>
  );
};

export default WorkModal;
